import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [feedbacks, setFeedbacks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchFeedbacks = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:5000/api/feedbacks", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.data.success) {
        setFeedbacks(response.data.feedbacks);
      } else {
        setError("Failed to load feedbacks");
      }
    } catch (err) {
      setError("Error fetching feedbacks");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFeedbacks();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this feedback?")) return;

    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:5000/api/feedbacks/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      // Refresh list after deletion
      setFeedbacks(feedbacks.filter((fb) => fb._id !== id));
    } catch (error) {
      alert("Failed to delete feedback");
    }
  };

  if (loading) return <p className="text-center mt-10 text-lg">Loading feedbacks...</p>;
  if (error) return <p className="text-center mt-10 text-red-600">{error}</p>;

  return (
    <div className="min-h-screen bg-gradient-to-tr from-purple-300 via-indigo-300 to-blue-300 flex flex-col items-center px-4 py-8">
      <div className="max-w-7xl w-full bg-white/90 backdrop-blur-md rounded-2xl shadow-2xl p-8 border border-indigo-200">
        <h1 className="text-4xl font-extrabold mb-8 text-center text-indigo-900">
          Student Feedback Dashboard
        </h1>

        {feedbacks.length === 0 ? (
          <p className="text-center text-gray-600 text-lg">No feedbacks found.</p>
        ) : (
          <div className="space-y-6">
            {feedbacks.map((fb) => (
              <div
                key={fb._id}
                className="border rounded-lg p-6 shadow-md bg-white flex flex-col md:flex-row md:justify-between md:items-center"
              >
                <div>
                  <h2 className="text-xl font-semibold text-indigo-900">
                    {fb.name} <span className="text-sm text-gray-500">({fb.email})</span>
                  </h2>
                  <p>
                    <strong>Course:</strong> {fb.course}
                  </p>
                  <p>
                    <strong>Rating:</strong>{" "}
                    <span className="text-yellow-500">{Array(fb.rating).fill("⭐").join("")}</span>
                    {" "} ({fb.rating}/5)
                  </p>
                  <p>
                    <strong>Comments:</strong> {fb.comments || "No comments"}
                  </p>
                  <p className="text-sm text-gray-400 mt-1">
                    Submitted on {new Date(fb.createdAt).toLocaleString()}
                  </p>
                </div>

                <button
                  onClick={() => handleDelete(fb._id)}
                  className="mt-4 md:mt-0 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition"
                >
                  Delete
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
