import { useState } from 'react';
import axios from 'axios';

export default function FeedbackForm() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    course: '',
    rating: 5,
    comments: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/feedbacks', formData);
      window.location.href = '/thankyou';
    } catch (error) {
      alert('Error submitting feedback!');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 via-white to-blue-100 flex items-center justify-center px-4">
      <div className="w-full max-w-lg bg-white rounded-3xl shadow-2xl p-10 border border-gray-200 transition-all duration-300">
        <h1 className="text-3xl font-extrabold text-center text-blue-700 mb-8">
          Student Feedback Form
        </h1>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-gray-600 font-medium mb-2">Name</label>
            <input
              type="text"
              required
              className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            />
          </div>

          <div>
            <label className="block text-gray-600 font-medium mb-2">Email</label>
            <input
              type="email"
              required
              className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
            />
          </div>

          <div>
            <label className="block text-gray-600 font-medium mb-2">Course</label>
            <input
              type="text"
              required
              className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
              onChange={(e) => setFormData({ ...formData, course: e.target.value })}
            />
          </div>

          <div>
            <label className="block text-gray-600 font-medium mb-2">Rating (1-5)</label>
            <input
              type="number"
              min="1"
              max="5"
              required
              className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
              onChange={(e) => setFormData({ ...formData, rating: e.target.value })}
            />
          </div>

          <div>
            <label className="block text-gray-600 font-medium mb-2">Comments</label>
            <textarea
              rows="4"
              className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 transition resize-none"
              onChange={(e) => setFormData({ ...formData, comments: e.target.value })}
            />
          </div>

          <button
            type="submit"
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 rounded-xl shadow-lg transition duration-300"
          >
            Submit Feedback
          </button>
        </form>
      </div>
    </div>
  );
}
