// src/pages/AdminDashboard.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [feedbacks, setFeedbacks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFeedbacks = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/feedbacks");
        if (response.data.success) {
          setFeedbacks(response.data.feedbacks); // ✅ this is where you were wrong earlier
        } else {
          console.error("Failed to fetch feedbacks");
        }
      } catch (error) {
        console.error("❌ Error fetching feedbacks:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchFeedbacks();
  }, []);

  if (loading) return <p className="text-center">Loading feedbacks...</p>;

  return (
    <div className="max-w-4xl mx-auto mt-10">
      <h1 className="text-3xl font-bold mb-6 text-center">Student Feedbacks</h1>
      {feedbacks.length === 0 ? (
        <p className="text-center text-gray-500">No feedbacks found.</p>
      ) : (
        <div className="space-y-4">
          {feedbacks.map((fb) => (
            <div key={fb._id} className="border rounded-lg p-4 shadow-md bg-white">
              <h2 className="text-xl font-semibold">{fb.name} ({fb.email})</h2>
              <p><strong>Course:</strong> {fb.course}</p>
              <p><strong>Rating:</strong> {fb.rating}/5</p>
              <p><strong>Comments:</strong> {fb.comments || "No comments"}</p>
              <p className="text-sm text-gray-500">
                Submitted on {new Date(fb.createdAt).toLocaleString()}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
