import { useEffect, useState } from "react";
import axios from "axios";
import { Trash2 } from "lucide-react";

export default function Dashboard() {
  const [feedbacks, setFeedbacks] = useState([]);

  // Fetch feedbacks from backend
  const fetchFeedbacks = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/feedbacks");
      setFeedbacks(res.data.feedbacks);
    } catch (error) {
      console.error("‚ùå Fetching feedbacks failed:", error);
    }
  };

  // Delete feedback by ID
  const deleteFeedback = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/feedbacks/${id}`);
      setFeedbacks((prev) => prev.filter((fb) => fb._id !== id));
    } catch (error) {
      console.error("‚ùå Deletion failed:", error);
    }
  };

  useEffect(() => {
    fetchFeedbacks();
  }, []);

  return (
    <div className="min-h-screen px-4 py-10 bg-gradient-to-br from-[#f4f9ff] to-[#e8f0fe]">
      <div className="max-w-5xl mx-auto">
        <h1 className="text-4xl font-bold text-center text-blue-900 mb-10">üìã Student Feedback Dashboard</h1>

        {feedbacks.length === 0 ? (
          <p className="text-center text-gray-600">No feedbacks available.</p>
        ) : (
          <div className="grid gap-6 sm:grid-cols-2 md:grid-cols-3">
            {feedbacks.map((feedback) => (
              <div
                key={feedback._id}
                className="bg-white rounded-2xl shadow-lg border border-blue-100 p-5 relative hover:shadow-2xl transition-all duration-300"
              >
                <h2 className="text-xl font-semibold text-blue-800">{feedback.name}</h2>
                <p className="text-sm text-gray-600">{feedback.email}</p>
                <p className="mt-2 text-blue-600 font-medium">Course: {feedback.course}</p>
                <p className="text-yellow-600">‚≠ê Rating: {feedback.rating}</p>
                {feedback.comments && <p className="mt-2 text-gray-700">üí¨ {feedback.comments}</p>}

                <button
                  onClick={() => deleteFeedback(feedback._id)}
                  className="absolute top-3 right-3 text-red-500 hover:text-red-700"
                  title="Delete feedback"
                >
                  <Trash2 />
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
