import { useState } from 'react';
import axios from 'axios';

export default function FeedbackForm() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    course: '',
    rating: 5,
    comments: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/feedbacks', formData);
      window.location.href = '/thankyou';
    } catch (error) {
      alert('Error submitting feedback!');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-12 px-4">
      <div className="max-w-md mx-auto bg-white rounded-xl shadow-md overflow-hidden p-8">
        <h1 className="text-2xl font-bold text-center mb-6">Student Feedback</h1>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Name</label>
            <input 
              type="text" 
              className="w-full px-3 py-2 border rounded"
              required
              onChange={(e) => setFormData({...formData, name: e.target.value})}
            />
          </div>

          {/* Add email */}
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Email</label>
            <input 
              type="email" 
              className="w-full px-3 py-2 border rounded"
              required
              onChange={(e) => setFormData({...formData, email: e.target.value})}
            />
          </div>

          {/* Add course */}
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Course</label>
            <input 
              type="text" 
              className="w-full px-3 py-2 border rounded"
              required
              onChange={(e) => setFormData({...formData, course: e.target.value})}
            />
          </div>

          {/* Add rating */}
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Rating (1-5)</label>
            <input 
              type="number" 
              min="1" 
              max="5" 
              className="w-full px-3 py-2 border rounded"
              required
              onChange={(e) => setFormData({...formData, rating: e.target.value})}
            />
          </div>

          {/* Add comments */}
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Comments</label>
            <textarea 
              className="w-full px-3 py-2 border rounded"
              rows="4"
              onChange={(e) => setFormData({...formData, comments: e.target.value})}
            />
          </div>

          <button 
            type="submit" 
            className="w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
          >
            Submit Feedback
          </button>
        </form>
      </div>
    </div>
  );
}
