import React, { useEffect, useState } from "react";
import axios from "axios";

const Dashboard = () => {
  const [feedbacks, setFeedbacks] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  const fetchFeedbacks = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/feedbacks");
      setFeedbacks(res.data.feedbacks);
    } catch (err) {
      console.error("Error fetching feedbacks", err);
    }
  };

  const deleteFeedback = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/feedbacks/${id}`);
      setFeedbacks(feedbacks.filter((fb) => fb._id !== id));
    } catch (err) {
      console.error("Error deleting feedback", err);
    }
  };

  useEffect(() => {
    fetchFeedbacks();
  }, []);

  const filteredFeedbacks = feedbacks.filter((fb) =>
    fb.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    fb.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
    fb.course.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen p-6 bg-gradient-to-br from-[#f1f4f9] to-[#cdd6e4]">
      <h1 className="text-4xl font-bold text-center text-[#1a1a2e] mb-8">
        üìä Student Feedback Dashboard
      </h1>

      <div className="mb-6 max-w-md mx-auto">
        <input
          type="text"
          placeholder="üîç Search by name, email or course..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full p-3 rounded-lg border border-gray-300 shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-400"
        />
      </div>

      <div className="grid gap-6 grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4">
        {filteredFeedbacks.length > 0 ? (
          filteredFeedbacks.map((fb) => (
            <div
              key={fb._id}
              className="bg-gradient-to-br from-indigo-50 to-white hover:shadow-xl shadow-md rounded-2xl p-5 border border-indigo-100 transition duration-300 ease-in-out"
            >
              <h2 className="text-xl font-semibold text-indigo-700 mb-1">{fb.name}</h2>
              <p className="text-sm text-gray-500 mb-1">{fb.email}</p>
              <p className="text-sm text-gray-600 mb-2">üìö {fb.course}</p>
              <p className="text-yellow-500 text-lg mb-2">‚≠ê {fb.rating}</p>
              {fb.comments && (
                <p className="text-gray-700 italic mb-3">üí¨ "{fb.comments}"</p>
              )}
              <button
                onClick={() => deleteFeedback(fb._id)}
                className="mt-auto bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded-lg text-sm transition"
              >
                üóëÔ∏è Delete
              </button>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-600 col-span-full">No feedbacks found.</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
